import UIKit

protocol CalculatorBusinessLogic
{
    func calculateResult(numberBTC: Double)
    func setCurrencyNameConvert()
}

protocol CalculatorDataStore
{
    var currencyRatePerBTC: CurrencyRatePerBTC? { get set }
}

class CalculatorInteractor: CalculatorBusinessLogic, CalculatorDataStore
{
    var presenter: CalculatorPresentationLogic?
    var worker: CalculatorWorker = CalculatorWorker()
    
    var currencyRatePerBTC: CurrencyRatePerBTC? = nil
    
    // MARK: CalculatorBusinessLogic
    func calculateResult(numberBTC: Double) {
        let response = Calculator.RatePerBTC(currencyRatePerBTC: currencyRatePerBTC, numberBTC: numberBTC)
        presenter?.presentCalculateResult(response: response)
    }
    
    func setCurrencyNameConvert() {
        presenter?.presentSetCurrencyNameConvert(currencyRatePerBTC: currencyRatePerBTC)
    }
}
